
package Task1;

public class casting_with_wrapper{
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        Integer intObj = 50;
        Double doubleObj = (double) intObj;

        System.out.println("Wrapper Casting:");
        System.out.println("Integer object: " + intObj);
        System.out.println("Casted to Double object: " + doubleObj);
    }
	}

